import Cookies from "js-cookie";
let credMng = {
  credentialKeySave: data => Cookies.set("_jwtApiToken", data),
  credentialKeyTake: () => Cookies.get("_jwtApiToken"),
  credentialKeyRemove: () => Cookies.remove("_jwtApiToken"),
  axiosHeaderToken: () => {
    return {
      headers: {
        Accept: "application/json",
        "X-Requested-With": "XMLHttpRequest",
        Authorization: `Bearer ${Cookies.get("_jwtApiToken")}`
      },
      withCredentials: true
    }
  }
};
export default credMng;
