const credMng = {
  credentialKeySave: data => localStorage.setItem("_jwtApiToken", data),
  credentialKeyTake: () => localStorage.getItem("_jwtApiToken"),
  credentialKeyRemove: () => localStorage.removeItem("_jwtApiToken"),
  axiosHeaderToken: () => {
    return {
      headers: {
        Accept: "application/json",
        Authorization: `Bearer ${this.credentialKeyTake()}`
      }
    }
  }
};
export default credMng;
